<?php

require "Database.php";

if($_SERVER["REQUEST_METHOD"] == "POST"){
  $valuesNotEmpty = isset($_POST["email"]) && isset($_POST["password"]);
  $emailIsValid = filter_var($_POST["email"], FILTER_VALIDATE_EMAIL);
  if($emailIsValid && $valuesNotEmpty){
    $name = htmlspecialchars($_POST["email"]);
    $password = htmlspecialchars($_POST["password"]);
    $database = new Database();
    $database->registerUser($name, $password);
  }
}
?>



