<?php

require "Database.php";

session_abort();

if ($_SERVER["REQUEST_METHOD"] == "POST") {
    $valuesNotEmpty = isset($_POST["email"]) && isset($_POST["password"]);
    $emailIsValid = filter_var($_POST["email"], FILTER_VALIDATE_EMAIL);
    if ($valuesNotEmpty && $emailIsValid) {
        $database = new Database();
        $user = $database->getUserByEmail(htmlspecialchars($_POST["email"]));
        var_dump($user);
        if (password_verify($_POST["password"], $user["password"])) {
            session_start();
            $_SESSION["email"] = $user["email"];
            $_SESSION["privilege"] = $user["privilege"];
            header("location: afterLogin.php");
            exit();
        }else{
            echo "Zadali jste špatné heslo!";
        }

    }
}

?>


